import { useRef, useState, useEffect } from "react"
import { BeakerIcons } from "@heroicons/react/outline"
import { IconCheck } from "./Icons"

const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/
const REGISTER_URL = 'http://localhost:8080/api/user/register'

const RegistrationForm = () => {
  const userRef = useRef()
  const errRef = useRef()

  const [firstName, setFirstName] = useState('')
  const [validFirstName, setValidFirstName] = useState(false)
  const [firstNameFocus, setFirstNameFocus] = useState(false)

  const [lastName, setLasttName] = useState('')
  const [validLasttName, setValidLasttName] = useState(false)
  const [lasttNameFocus, setLasttNameFocus] = useState(false)

  const [userName, setUserName] = useState('')
  const [validUserName, setValidUserName] = useState(false)
  const [userNameFocus, setUserNameFocus] = useState(false)

  const [pwd, setPwd] = useState('')
  const [validPwd, setValidPwd] = useState(false)
  const [pwdFocus, setPwdFocus] = useState(false)

  const [matchPwd, setMatchPwd] = useState('')
  const [validMatch, setValidMatch] = useState(false)
  const [matchFocus, setMatchFocus] = useState(false)

  const [favoriteGenres, setFavoriteGenres] = useState('')
  const [validFavoriteGenres, setValidFavoriteGenres] = useState(false)
  const [favoriteGenresFocus, setFavoriteGenresFocus] = useState(false)

  const [avgReadingHrsDaily, setAvgReadingHrsDaily] = useState('')
  const [validAvgReadingHrsDaily, setValidAvgReadingHrsDaily] = useState(false)
  const [avgReadingHrsDailyFocus, setAvgReadingHrsDailyFocus] = useState(false)

  const [errMsg, setErrMsg] = useState('')
  const [success, setSuccess] = useState(false)

  useEffect(() => {
    userRef.current.focus()
  }, [])

  useEffect(() => {
    setValidUserName(USER_REGEX.test(userName))
  }, [userName])

  useEffect(() => {
    setValidPwd(PWD_REGEX.test(pwd))
    setValidMatch(pwd === matchPwd)
  }, [pwd, matchPwd])

  useEffect(() => {
    setErrMsg('')
  }, [userName, pwd, matchPwd])

  return (
    <section>
      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
      <h1 className="text-center text-4xl text-gray-500 mb-10">Register</h1>
      <form className="w-128 flex justify-center flex-col self-center shadow-md bg-white rounded px-8 pt-6 pb-8 mb-4 font-['Roboto]">
        <div className="flex mb-3">
        <div className="flex-1">
        <label htmlFor="username" className="block text-gray-700 text-sm mb-2">
          Username
          <IconCheck className={validUserName ? "vaild" : "invisible"} />
          <IconX className={validUserName || !userName ? "invisible" : "invalid"}></IconX>
        </label>
        <input 
          type="text" 
          id="username"
          ref={userRef}
          autoComplete="off"
          onChange={(e) => setUserName(e.target.value)}
          value={userName}
          require
          aria-invalid={validUserName ? "false" : "true"}
          aria-describedby="uinote"
          onFocus={() => setUserNameFocus(true)}
          onBlur={() => setUserNameFocus(false)}
          className={"w-full shadow appearance-none border rounded py-2 px-2 text-gray-700 leading-tight focus:outline-none focus:shadow-outline placeholder-red-700 text-base rounded-lg "}
        />
        <p id="uidnote" className={userNameFocus && userName && !validUserName ? "instructions" : "offscreen"}>
            4 to 24 characters.<br />
            Must begin with a letter.<br />
            Letters, numbers, underscores, hyphens allowed.
        </p>
        </div>
        <div className="flex-1 pl-3">
        <label htmlFor="pwd" className="block text-gray-700 text-sm mb-2">
            Password:
            <span className={validPwd ? "valid" : "invisible"}></span>
            <span className={validPwd || !pwd ? "invisible" : "invalid"}></span>
        </label>
        <input
            type="password"
            id="password"
            onChange={(e) => setPwd(e.target.value)}
            value={pwd}
            required
            aria-invalid={validPwd ? "false" : "true"}
            aria-describedby="pwdnote"
            onFocus={() => setPwdFocus(true)}
            onBlur={() => setPwdFocus(false)}
            className={"shadow appearance-none border rounded w-full py-2 px-2 text-gray-700 leading-tight focus:outline-none focus:shadow-outline placeholder-red-700 text-base rounded-lg "}
        />
        <p id="pwdnote" className={pwdFocus && !validPwd ? "instructions" : "offscreen"}>
            8 to 24 characters.<br />
            Must include uppercase and lowercase letters, a number and a special character.<br />
            Allowed special characters: <span aria-label="exclamation mark">!</span> <span aria-label="at symbol">@</span> <span aria-label="hashtag">#</span> <span aria-label="dollar sign">$</span> <span aria-label="percent">%</span>
        </p>
        </div>
        </div>
      </form>
    </section>
  )
}

export default RegistrationForm
