import { useState, useEffect } from 'react'
import axios from "../api/axios"
import {
    faStar, faComments, faHeart
} from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"

const SearchForm = () => {

    // const [ query, setQuery ] = useState('')
    const [allData,setAllData] = useState([]);
    const [filteredData,setFilteredData] = useState(allData);
    // const [ search, setSearch ] = useState('')

    const handleSearch = (event) =>{
        const value = event.target.value();
        let result = [];
        console.log(value);
        result = allData.filter((data) => {
        return data.title.search(value) != -1;
        });
        setFilteredData(result);
    }

    const SEARCH_URL = "/api/search/query?q=" + value

    useEffect(() => {
        axios.get(SEARCH_URL)
        .then(response => {
            console.log(response.data.docs)
            setAllData(response.data.docs);
            setFilteredData(response.data.docs);
        })
        .catch(error => {
            console.log('Error getting data: ' + error);
        })
    }, [])
    return (
        <>
            <form className="w-full flex flex-col justify-content self-center">
                <label htmlFor="search" className="w-96 relative block self-center my-6">
                    <span className="sr-only">Search</span>
                    <span className="absolute inset-y-0 left-0 flex items-center pl-2 pointer-events-none">
                        <svg className="w-5 h-5 text-gray-500" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fillRule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clipRule="evenodd"></path></svg>
                    </span>
                    <input onChange={(event) =>handleSearch(event)} type="text" placeholder="Search for a book..." name="search" className="rounded-full placeholder:italic placeholder:text-slate-400 block bg-white w-full border border-slate-300 round-full py-2 pl-9 pr-3 shadow-sm focus:outline-none focus:ring-sky-500 focus:ring-1 sm:text-sm" />
                </label>
                <button type="button" className="flex justify-center self-center w-40 text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-lg px-5 py-2.5 text-center mr-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Search</button>
            </form>
            <section className="grid grid-cols-5 gap-6">
                {filteredData.map((value,index)=>{
                    return (
                        <div key={value.id} className="">
                            <div style={{width: '100%', height: '45vh'}}>
                                <img src={value.coverArtUrl} alt="" className="" style={{width: '100%', height: '100%'}}/>
                            </div>
                            <h3 className="text-2xl capitalize pt-3 font-bold text-gray-600">{value.authorName}</h3>
                            <h6 className="text-lg italic">{value.authorName}</h6>
                            <p>{value.authorNamer}</p>
                            <div className="flex gap-2 pt-3 ">
                                <FontAwesomeIcon icon={faStar} className="text-yellow-500"/>
                                <FontAwesomeIcon icon={faStar} className="text-yellow-500"/>
                                <FontAwesomeIcon icon={faStar} className="text-yellow-500"/>
                                <FontAwesomeIcon icon={faStar} className="text-yellow-500"/>
                                <FontAwesomeIcon icon={faStar} className="text-yellow-500"/>
                            </div>
                            <div className="flex justify-between pt-3">
                                <FontAwesomeIcon icon={faComments} />
                                <FontAwesomeIcon icon={faHeart} />
                            </div>
                        </div>
                    )
                })}
            </section>
        </>
    )
}

export default SearchForm
