import { useRef, useState, useEffect } from "react"
import axios from './api/axios'

const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/
const REGISTER_URL = 'http://localhost:8080/api/user/register'

const RegistrationForm = () => {
  const userRef = useRef()
  const errRef = useRef()

  const [firstName, setFirstName] = useState('')
  const [validFirstName, setValidFirstName] = useState(false)
  const [firstNameFocus, setFirstNameFocus] = useState(false)

  const [lastName, setLasttName] = useState('')
  const [validLasttName, setValidLasttName] = useState(false)
  const [lasttNameFocus, setLasttNameFocus] = useState(false)

  const [userName, setUserName] = useState('')
  const [validUserName, setValidUserName] = useState(false)
  const [userNameFocus, setUserNameFocus] = useState(false)

  const [password, setPassword] = useState('')
  const [validPassword, setValidPassword] = useState(false)
  const [passwordFocus, setPasswordFocus] = useState(false)

  const [matchPwd, setMatchPwd] = useState('')
  const [validMatch, setValidMatch] = useState(false)
  const [matchFocus, setMatchFocus] = useState(false)

  const [favoriteGenres, setFavoriteGenres] = useState('')
  const [validFavoriteGenres, setValidFavoriteGenres] = useState(false)
  const [favoriteGenresFocus, setFavoriteGenresFocus] = useState(false)

  const [avgReadingHrsDaily, setAvgReadingHrsDaily] = useState('')
  const [validAvgReadingHrsDaily, setValidAvgReadingHrsDaily] = useState(false)
  const [avgReadingHrsDailyFocus, setAvgReadingHrsDailyFocus] = useState(false)

  useEffect(() => {
    userRef.current.focus()
  }, [])

  useEffect(() => {
    setValidName(USER_REGEX.test(userName))
  }, [userName])

  useEffect(() => {
    setValidPwd(PWD_REGEX.test(password))
    setValidMatch(pssword === matchPwd)
  }, [password, matchPwd])

  useEffect(() => {
    setErrMsg('')
  }, [userName, password, matchPwd])

  return (
    <section>
      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
      <h1>Register</h1>
      <form>
        <label htmlFor="username">
          Username
          <span className={validUserName ? "vaild" : "hide"}></span>
          <span className={validUserName || !userName ? "hide" : "invalid"}></span>
        </label>
        <input 
          type="text" 
          id="username"
          ref={userRef}
          autoComplete="off"
          onChange={(e) => setUserName(e.target.value)}
          value={userName}
          require
          aria-invalid={validUserName ? "false" : "true"}
          aria-describedby="uinote"
          onFocus={() => setUserNameFocus(true)}
          onBlur={() => setUserNameFocus(false)}
        />
        <p id="uidnote" className={userNameFocus && userNamw && !validUserName ? "instructions" : "offscreen"}>
            4 to 24 characters.<br />
            Must begin with a letter.<br />
            Letters, numbers, underscores, hyphens allowed.
        </p>

        <label htmlFor="password">
            Password:
            <span className={validPassword ? "valid" : "hide"}></span>
            <span className={validPassword || !password ? "hide" : "invalid"}></span>
        </label>
        <input
            type="password"
            id="password"
            onChange={(e) => setPwd(e.target.value)}
            value={password}
            required
            aria-invalid={validPassword ? "false" : "true"}
            aria-describedby="pwdnote"
            onFocus={() => setPasswordFocus(true)}
            onBlur={() => setPasswordFocus(false)}
        />
        <p id="pwdnote" className={passwordFocus && !validpassword ? "instructions" : "offscreen"}>
            8 to 24 characters.<br />
            Must include uppercase and lowercase letters, a number and a special character.<br />
            Allowed special characters: <span aria-label="exclamation mark">!</span> <span aria-label="at symbol">@</span> <span aria-label="hashtag">#</span> <span aria-label="dollar sign">$</span> <span aria-label="percent">%</span>
        </p>
      </form>
    </section>
  )
}

export default RegistrationForm
